$(document).ready(function(){var $form=$('form#sendMeOrder');$form.get(0).onsubmit=function(e){
        e.preventDefault();var popupFormData=$form.serialize();
        $.ajax({url:'/mail.php',type:'POST',data:popupFormData,dataType:'json',beforeSend:function(){},
            success:function(){alert("Мы постараемся связаться с вами как можно быстрее");},
            error:function(){alert("Введите как минимум ваш номер телефона, чтобы мы могли с вами связаться.");},
            complete:function(){$('#call-modal').removeClass('md-show');}});};
    $('.btn.modal').click(function(){var modal=$('#'+$(this).data('modal'));$(modal).addClass('md-show'); });
    $('.md-close').click(function(){$(this).parents().removeClass('md-show');});
    var swiper=new Swiper(".swiper-container",{
        pagination:".swiper-pagination",slidesPerView:3,paginationClickable:!0,spaceBetween:100,
        nextButton:".swiper-button-next",prevButton:".swiper-button-prev",grabCursor:!0,loop:!0});
    (function(){if(window.pluso)if(typeof window.pluso.start=="function")return;
        if (window.ifpluso==undefined){window.ifpluso=1;
            var d=document,s=d.createElement('script'),g='getElementsByTagName';
            s.type='text/javascript';s.charset='UTF-8';s.async=true;
            s.src=('https:'==window.location.protocol?'https':'http')+'://share.pluso.ru/pluso-like.js';
            var h=d[g]('body')[0];h.appendChild(s);}})();});